actions.script:
Calendar nowCalendar = ec.user.nowCalendar
Calendar acquiredCal = nowCalendar.clone() acquiredCal.setTime(asset.acquiredDate) Calendar expectedEndOfLifeCal = nowCalendar.clone() expectedEndOfLifeCal.setTime(asset.expectedEndOfLife)
usefulLifeYears = expectedEndOfLifeCal.get(Calendar.YEAR) - acquiredCal.get(Calendar.YEAR) /* use integer number of years, partial year depreciation is not common and should be needed (may cause issues) int days = expectedEndOfLifeCal.get(Calendar.DAY_OF_YEAR) - acquiredCal.get(Calendar.DAY_OF_YEAR) // NOTE: using 365 days isn't always correct, should use number of days in expectedEndOfLife year, but shouldn't matter much int daysInYear = 365 if (days < 0) { yearsInt = yearsInt - 1; days = daysInYear + days } usefulLifeYears = (yearsInt as BigDecimal) + days/daysInYear */
Calendar txCal = nowCalendar.clone() txCal.setTime(transactionDate) yearsRemaining = expectedEndOfLifeCal.get(Calendar.YEAR) - txCal.get(Calendar.YEAR) // if we are before the day in the year of end of life, add 1 year (we haven't gone to the next year for this asset) boolean txBeforeDayOfYear = false if (txCal.get(Calendar.DAY_OF_YEAR) < expectedEndOfLifeCal.get(Calendar.DAY_OF_YEAR) - 1) { yearsRemaining = yearsRemaining + 1I txBeforeDayOfYear = true }
// see if it is the last period for each depreciation year (based on Asset.expectedEndOfLife) Calendar nextMonthCal = txCal.clone() nextMonthCal.add(Calendar.MONTH, 1) isLastYearPeriod = (txBeforeDayOfYear && nextMonthCal.get(Calendar.DAY_OF_YEAR) > expectedEndOfLifeCal.get(Calendar.DAY_OF_YEAR))
